sys_script_client.script:
function onLoad() {
    if (g_form.isNewRecord()) {
        return;
    }
    var ga = new GlideAjax('JobUtilsScheduledJobAjaxHelper');
    ga.addParam('sysparm_name', 'getScheduledJobImportReport');
    ga.addParam('scheduledJobId', g_form.getUniqueValue());
	ga.addParam('tableName', g_form.getTableName());
    ga.getXML(updateProgress);
}

function updateProgress(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.clearMessages();
    if (answer) {
        var obj = answer.evalJSON(true);
        if(obj && obj.reportHtml){
			//jQuery('#scheduled-job-utilities-report').text(obj.reportHtml);
            g_form.addInfoMessage(obj.reportHtml);
            setTimeout(onLoad, 5000);
        }

    }
}
