sys_script_include.script:
var JobUtilsExecutionContext = Class.create();
JobUtilsExecutionContext.prototype = {
	initialize: function (executionContext) {
		if (executionContext) {
			this.setFromModel(executionContext);
		}
	},

	//Model Values
	jobName: '',
	inProgress: false,
	status: '',
	percentage: '',
	age: {
		display: '',
		type: ''
	},
	startTime: '',
	endTime: '',
	contextRecord: {
		sysId: '',
		display: '',
		table: '',
		link: '',
	},

	//Functions

	setJobName: function(name){
		this.jobName = name;
	},

	setJobInProgress: function (inProgress, status) {
		this.inProgress = inProgress || false;
		this.status = status;
	},

	setPercent: function (percent) {
		this.percentage = percent;
	},

	setStartTime: function (gdt) {
		if (!gdt) {
			return;
		}
		this.startTime = gdt.getDisplayValue();
		this.startTimeNumeric = gdt.getNumericValue();
		if (new GlideDateTime() > gdt) {
			this.age = {
				display: GlideDateTime.subtract(gdt, new GlideDateTime()).getDisplayValue(),
				type: 'past'
			}
		} else {
			this.age = {
				display: GlideDateTime.subtract(new GlideDateTime(), gdt).getDisplayValue(),
				type: 'future'
			}
		}
	},

	setEndTime: function (gdt) {
		if (!gdt) {
			return;
		}
		this.endTime = gdt.getDisplayValue();
	},

	setContextRecord: function (gr) {
		if (!gr) {
			return;
		}
		this.contextRecord = {
			sysId: gr.getUniqueValue(),
			display: gr.getDisplayValue(),
			table: gr.getTableName(),
			link: gr.getLink(),
		}
	},

	setStatistic: function (name, value) {
		if (!this.statistics) {
			this.statistics = [];
		}

		for (var i = 0; i < this.statistics.length; i++) {
			var stat = this.statistics[i];
			if (stat.name === name) {
				stat.value = value;
				return;
			}
		}
		//No existing stat. Adding new one.

		this.statistics.push({
			name: name,
			value: value
		});

	},

	getFormDisplay: function () {
		var report = new JobUtilsFormReport();
		if(this.percentage){
			report.setTitle('Scheduled Job Utilities - In Progress (' + parseInt(this.percentage) + '%)');
		}else{
			report.setTitle('Scheduled Job Utilities - In Progress');
		}

		var primarySection = report.addSection('primarySection');
		var txt = '';
		if (this.inProgress) {
			if(this.jobName){
				txt = this.jobName;
			}else{
				txt = 'This job';
			}
			txt += ' is currently in progress and has been running for ' + this.age.display + '.';
			if (this.contextRecord && this.contextRecord.link) {
				txt += ' <a href="' + this.contextRecord.link + '">' + this.contextRecord.display + '</a> (' + this.status + ')';
			}
			primarySection.addParagraph(txt);
		}

		if (Array.isArray(this.statistics) && this.statistics.length > 0) {
			var statistics = report.addSection('statistics');
			var ul = statistics.addUnorderedList();
			for(var i = 0; i < this.statistics.length; i++){
				var stat = this.statistics[i];
				ul.push('<bold>' + stat.name + '</bold>: ' + stat.value.toString());
			}
		}
		var html = report.build();
		return html;
	},

	//Internal Functions
	setFromModel: function (model) {
		for (var key in model) {
			this[key] = model[key];
		}
	},

	type: 'JobUtilsExecutionContext'
};
