	getScheduledJobImportReport: function(){
		var scheduledJobId = this.getParameter('scheduledJobId');
		var tableName = this.getParameter('tableName');
		if (scheduledJobId && tableName) {
			var controller = new JobUtilsController();
			var scheduledJob = controller.getScheduledJob(scheduledJobId, tableName);
			if(scheduledJob.executionContexts && scheduledJob.executionContexts[0] && scheduledJob.executionContexts[0].inProgress){
				var html = scheduledJob.executionContexts[0].getFormDisplay();
				var obj = {
					reportHtml: html
				};
				return JSON.stringify(obj);
			}
		}
	},


